import { useState, useEffect, useRef } from "react";
import { useConversation } from "@11labs/react";
import { Button } from "@/components/ui/button";
import { Mic, MicOff, MessageCircle, AlertCircle, RefreshCw } from "lucide-react";
import Lottie from "lottie-react";
import mimiAnimation from "@/assets/animations/mimi-animation.json";

interface ConversationalAgentProps {
  onStoryRequest: (request: string) => void;
}

const ConversationalAgent = ({ onStoryRequest }: ConversationalAgentProps) => {
  const [storyRequest, setStoryRequest] = useState("");
  const [conversationId, setConversationId] = useState<string | null>(null);
  const [connectionError, setConnectionError] = useState<string | null>(null);
  const [isReconnecting, setIsReconnecting] = useState(false);
  const reconnectTimeoutRef = useRef<number | null>(null);
  const pingIntervalRef = useRef<number | null>(null);
  const lastPingTimeRef = useRef<number>(0);
  const reconnectAttemptsRef = useRef(0);

  const conversation = useConversation({
    onConnect: () => {
      console.log("Connected to ElevenLabs agent");
      setConnectionError(null);
      setIsReconnecting(false);
      reconnectAttemptsRef.current = 0;
      
      // Setup ping interval to keep connection alive
      if (pingIntervalRef.current) {
        clearInterval(pingIntervalRef.current);
      }
      
      pingIntervalRef.current = window.setInterval(() => {
        lastPingTimeRef.current = Date.now();
        // Check if connection is still alive
        if (conversation.status === "connected") {
          console.log("Ping: Connection health check");
        }
      }, 30000); // Check every 30 seconds
    },
    onDisconnect: () => {
      console.log("Disconnected from ElevenLabs agent");
      setConversationId(null);
      
      // Clear ping interval
      if (pingIntervalRef.current) {
        clearInterval(pingIntervalRef.current);
        pingIntervalRef.current = null;
      }
      
      // If not manually disconnected and not reconnecting, attempt to reconnect
      if (!isReconnecting && reconnectAttemptsRef.current < 3) {
        handleReconnect();
      }
    },
    onMessage: (message) => {
      console.log("Received message:", message);
      
      // Handle messages according to ElevenLabs docs - be very careful not to break the flow
      if (message && typeof message === 'object') {
        const messageData = message as any;
        
        // Handle user transcription - just log, don't process
        if (messageData.type === 'user_transcript') {
          console.log("User transcript:", messageData.user_transcript);
          return;
        }
        
        // Handle agent response - extract story requests
        if (messageData.type === 'agent_response') {
          console.log("Agent response:", messageData.agent_response);
          
          const responseText = messageData.agent_response?.trim();
          if (responseText && responseText.length > 30 && 
              !responseText.toLowerCase().includes('hi') && 
              !responseText.toLowerCase().includes('hello') &&
              !responseText.toLowerCase().includes("i'm mimi")) {
            setStoryRequest(responseText);
            onStoryRequest(responseText);
          }
          return;
        }
        
        // Handle audio data - just log
        if (messageData.type === 'audio') {
          console.log("Audio data received");
          return;
        }
        
        // Handle other message types without breaking flow
        if (messageData.type === 'interruption') {
          console.log("Interruption detected");
          return;
        }
        
        if (messageData.type === 'ping' || messageData.type === 'pong') {
          console.log("Connection health check:", messageData.type);
          return;
        }
        
        if (messageData.type === 'error') {
          console.error("Agent error:", messageData.error);
          return;
        }
        
        // Handle legacy format for backward compatibility
        if (messageData.source && messageData.message) {
          if (messageData.source === "user") {
            console.log("User message (legacy):", messageData.message);
            return;
          }
          
          if (messageData.source === "ai") {
            console.log("AI response (legacy):", messageData.message);
            const responseText = messageData.message.trim();
            if (responseText.length > 30 && 
                !responseText.toLowerCase().includes('hi') && 
                !responseText.toLowerCase().includes('hello') &&
                !responseText.toLowerCase().includes("i'm mimi")) {
              setStoryRequest(responseText);
              onStoryRequest(responseText);
            }
            return;
          }
        }
        
        // Log unhandled messages for debugging
        console.log("Unhandled message type:", messageData.type || messageData);
      }
    },
    onError: (error) => {
      console.error("Conversation error:", error);
      
      // Set error message for user feedback
      if (typeof error === 'string') {
        setConnectionError(error);
      } else if (error instanceof Error) {
        setConnectionError(error.message);
      } else {
        setConnectionError("An unknown error occurred with the conversation");
      }
      
      // Don't automatically end conversation on all errors
      // Only attempt reconnect for connection-related errors
      if (error && (error.toString().includes('WebSocket') || 
                   error.toString().includes('connection') || 
                   error.toString().includes('network'))) {
        handleReconnect();
      }
    }
  });

  // Handle reconnection logic
  const handleReconnect = () => {
    setIsReconnecting(true);
    reconnectAttemptsRef.current += 1;
    
    console.log(`Attempting to reconnect (attempt ${reconnectAttemptsRef.current}/3)...`);
    
    // Clear any existing timeout
    if (reconnectTimeoutRef.current) {
      window.clearTimeout(reconnectTimeoutRef.current);
    }
    
    // Exponential backoff for reconnection attempts
    const backoffTime = Math.min(1000 * Math.pow(2, reconnectAttemptsRef.current), 10000);
    
    reconnectTimeoutRef.current = window.setTimeout(async () => {
      try {
        if (reconnectAttemptsRef.current <= 3) {
          await handleStartConversation(true);
        } else {
          setIsReconnecting(false);
          setConnectionError("Failed to reconnect after multiple attempts. Please try again later.");
        }
      } catch (error) {
        console.error("Reconnection failed:", error);
        setConnectionError("Failed to reconnect. Please try again manually.");
        setIsReconnecting(false);
      }
    }, backoffTime);
  };

  // Cleanup function for all timers and intervals
  useEffect(() => {
    return () => {
      if (reconnectTimeoutRef.current) {
        window.clearTimeout(reconnectTimeoutRef.current);
      }
      if (pingIntervalRef.current) {
        window.clearInterval(pingIntervalRef.current);
      }
    };
  }, []);

  const handleStartConversation = async (isReconnect = false) => {
    try {
      setConnectionError(null);
      if (!isReconnect) {
        setIsReconnecting(false);
        reconnectAttemptsRef.current = 0;
      }
      
      // Request microphone access first
      await navigator.mediaDevices.getUserMedia({ audio: true });
      
      // Start conversation with proper agent ID
      const sessionId = await conversation.startSession({ 
        agentId: "agent_01jxsmn9d5fr8arzbsy2cgbbt0",
        // Add connection timeout to prevent hanging connections
        timeout: 10000 // 10 seconds timeout
      });
      
      setConversationId(sessionId);
      console.log("Conversation started with ID:", sessionId);
    } catch (error) {
      console.error("Failed to start conversation:", error);
      setConnectionError("Failed to start conversation. Please check your microphone permissions and try again.");
      setIsReconnecting(false);
    }
  };

  const handleEndConversation = async () => {
    try {
      // Clear reconnection attempts and timers
      if (reconnectTimeoutRef.current) {
        window.clearTimeout(reconnectTimeoutRef.current);
        reconnectTimeoutRef.current = null;
      }
      if (pingIntervalRef.current) {
        window.clearInterval(pingIntervalRef.current);
        pingIntervalRef.current = null;
      }
      
      setIsReconnecting(false);
      reconnectAttemptsRef.current = 0;
      
      await conversation.endSession();
      setConversationId(null);
      setStoryRequest("");
      setConnectionError(null);
    } catch (error) {
      console.error("Failed to end conversation:", error);
      // Force reset the state even if the endSession call fails
      setConversationId(null);
      setIsReconnecting(false);
    }
  };

  return (
    <div className="space-y-6">
      <div className="text-center">
        <div className="w-48 h-48 mx-auto mb-4">
          <Lottie 
            animationData={mimiAnimation} 
            loop={true}
            autoplay={true}
            className={`transition-transform duration-300 ${
              conversation.isSpeaking 
                ? "animate-pulse scale-110" 
                : conversation.status === "connected" 
                ? "scale-105" 
                : "scale-100"
            }`}
          />
        </div>
        <h3 className="text-xl font-semibold mb-2">
          {conversation.status === "connected" ? "Mimi is listening..." : "Talk to Mimi"}
        </h3>
        <p className="text-gray-600 dark:text-gray-300 text-sm">
          Tell Mimi what kind of story you'd like to hear
        </p>
        {conversationId && (
          <p className="text-xs text-gray-500 mt-1">
            Session ID: {conversationId}
          </p>
        )}
      </div>

      <div className="flex justify-center gap-4">
        {conversation.status === "disconnected" ? (
          <Button
            onClick={() => handleStartConversation(false)}
            className="bg-gradient-to-r from-purple-500 to-pink-500 hover:from-purple-600 hover:to-pink-600 flex items-center gap-2"
            disabled={isReconnecting}
          >
            {isReconnecting ? (
              <>
                <RefreshCw className="w-4 h-4 animate-spin" />
                Reconnecting...
              </>
            ) : (
              <>
                <MessageCircle className="w-4 h-4" />
                Start Conversation
              </>
            )}
          </Button>
        ) : (
          <Button
            onClick={handleEndConversation}
            variant="destructive"
            className="flex items-center gap-2"
          >
            <MicOff className="w-4 h-4" />
            End Conversation
          </Button>
        )}
      </div>
      
      {connectionError && (
        <div className="p-4 bg-red-50 dark:bg-red-900/20 rounded-lg">
          <div className="flex items-center gap-2 mb-2">
            <AlertCircle className="w-4 h-4 text-red-600" />
            <span className="text-red-600 dark:text-red-400 font-medium">
              Connection Error
            </span>
          </div>
          <p className="text-sm text-red-600 dark:text-red-400">
            {connectionError}
          </p>
          <Button 
            variant="outline" 
            size="sm" 
            className="mt-2 text-red-600 border-red-200 hover:bg-red-50"
            onClick={() => handleStartConversation(false)}
            disabled={isReconnecting}
          >
            Try Again
          </Button>
        </div>
      )

      {conversation.status === "connected" && (
        <div className="p-4 bg-green-50 dark:bg-green-900/20 rounded-lg">
          <div className="flex items-center gap-2 mb-2">
            <Mic className="w-4 h-4 text-green-600" />
            <span className="text-green-600 dark:text-green-400 font-medium">
              Connected - Speak now!
            </span>
          </div>
          {conversation.isSpeaking && (
            <p className="text-sm text-green-600 dark:text-green-400">
              Mimi is speaking...
            </p>
          )}
        </div>
      )}

      {storyRequest && (
        <div className="p-4 bg-gradient-to-r from-purple-50 to-pink-50 dark:from-purple-900/20 dark:to-pink-900/20 rounded-lg">
          <h4 className="font-medium mb-2">Story Request Captured:</h4>
          <p className="text-sm text-gray-700 dark:text-gray-300 italic">
            "{storyRequest}"
          </p>
        </div>
      )}

      <div className="p-4 bg-blue-50 dark:bg-blue-900/20 rounded-lg text-sm">
        <p className="text-blue-700 dark:text-blue-300">
          ðŸ’¡ <strong>Tip:</strong> Just speak naturally! Ask for any kind of story - adventures, fairy tales, educational stories, or anything you can imagine.
        </p>
      </div>
    </div>
  );
};

